import React from 'react';

const SeasonDisplay = (props) =>{
    console.log('props', props);

    const season = getSeason(props.lat, new Date().getMonth());
    console.log('season', season);
    const text = getTextAndIconBasedOnSeason(season).text;
    const icon = this.getTextAndIconBasedOnSeason(season).icon;

    return(
        <div>
            <i className={`${icon} icon`}></i>
            <h1>{text}</h1>
            <i className={`${icon} icon`}></i>
        </div>
    );
}

const getSeason = (lat, month) => {
    if(month > 2 && month < 9){
        return lat > 0 ? 'Summmer' : 'Winter';
    }
    else{
        return lat > 0 ? 'Winter' : 'Summer';
    }
}

const getTextAndIconBasedOnSeason = (season) => {
    return season === 'Winter' ? {text: 'Burr, it is chilly', icon: 'snowlake'}: {text: 'Lets hit the beach', icon: 'sun'};
}

export default SeasonDisplay;
